import axios from 'axios';
export default {
  created: function created() {
    this.getLogs();
    this.getRegistrationCount();
    this.getRevenues();
    this.getOnlineCustomers();
    this.getBookingCounts();
    this.getUnapprovedBookings();
  },
  methods: {
    getLogs: function getLogs() {
      var _this = this;

      console.log('called getLogs');
      axios.get("/_system/php/api/customer/getLogs.php").then(function (response) {
        console.log('response: ' + JSON.stringify(response.data));
        _this.logs = response.data.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getRevenues: function getRevenues() {
      var _this2 = this;

      console.log('called getRevenues');
      axios.get("/_system/php/api/customer/getRevenuesMonthly.php").then(function (response) {
        console.log('response: ' + JSON.stringify(response.data));
        var ops = {
          xaxis: {
            categories: []
          }
        };
        ops.xaxis.categories = response.data.data.months;
        ApexCharts.exec("chartRevenues", "updateOptions", ops, false, true); //Update data series

        _this2.series.revenues[0].data = response.data.data.totals;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getRegistrationCount: function getRegistrationCount() {
      var _this3 = this;

      console.log('called getRegistrationCount');
      axios.get("/_system/php/api/customer/getRegistrationCountToday.php").then(function (response) {
        _this3.registration_count = response.data.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getOnlineCustomers: function getOnlineCustomers() {
      var _this4 = this;

      console.log('called getOnlineCustomers');
      axios.get("/_system/php/api/customer/getAllOnline.php").then(function (response) {
        console.log('response: ' + JSON.stringify(response.data));
        _this4.customers = response.data.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getBookingCounts: function getBookingCounts() {
      var _this5 = this;

      console.log('called getBookingCounts');
      axios.get("/_system/php/api/customer/getBookingCounts.php").then(function (response) {
        console.log('response: ' + JSON.stringify(response.data));
        var ops = {
          xaxis: {
            categories: []
          }
        };
        ops.xaxis.categories = response.data.data.months;
        ApexCharts.exec("chartBookings", "updateOptions", ops, false, true);
        _this5.series.bookings[0].data = response.data.data.counts;
      }).catch(function (error) {
        console.log(error);
      });
    },
    getUnapprovedBookings: function getUnapprovedBookings() {
      var _this6 = this;

      console.log('called getUnapprovedBookings');
      axios.get("/_system/php/api/customer/getUnapprovedBookings.php").then(function (response) {
        console.log('response: ' + JSON.stringify(response.data));
        _this6.bookings = response.data.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    approveBooking: function approveBooking() {
      console.log("Approved!");
    },
    rejectBooking: function rejectBooking() {
      console.log("Rejected");
    }
  },
  data: function data() {
    return {
      logs: "",
      registration_count: "",
      customers: "",
      bookings: "",
      options: {
        revenues: {
          chart: {
            id: "chartRevenues"
          },
          title: {
            text: "Monthly Revenues"
          }
        },
        bookings: {
          chart: {
            id: "chartBookings"
          },
          title: {
            text: "Monthly Bookings"
          }
        }
      },
      series: {
        revenues: [{
          name: "Total Revenue",
          data: []
        }],
        bookings: [{
          name: "Number of Bookings",
          data: []
        }]
      }
    };
  }
};
