export default {
  methods: {
    checkLimit: function checkLimit(addon) {
      var count = this.$store.getters.booking.addons.filter(function (inclusion) {
        return inclusion.addon_id == addon.addon_id;
      }).length;
      return count < addon.addon_max;
    },
    addAddon: function addAddon(addon) {
      if (this.checkLimit(addon)) {
        var availed_addon = {
          addon_id: addon.addon_id,
          addon_type: addon.addon_type,
          addon_description: addon.addon_description,
          addon_price: addon.addon_price,
          addon_source: "BOOKING" // 'AVAILED'

        };
        this.$store.commit('addBookingAddon', availed_addon);
      }
    }
  }
};
