import axios from "axios";
export default {
  data: function data() {
    return {
      salesReportsByDate: "",
      calendarMode: "Day",
      sales: [],
      pageNum: 0,
      pageCount: 0,
      searchbar: "",
      totalprice: 0
    };
  },
  mounted: function mounted() {
    this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    this.getPageCount();
  },
  methods: {
    goToPage: function goToPage(pageNum) {
      console.log(pageNum);
      this.pageNum = pageNum;
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    calendarModeDay: function calendarModeDay() {
      this.calendarMode = "Day";
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    calendarModeWeek: function calendarModeWeek() {
      this.calendarMode = "Week";
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    calendarModeMonth: function calendarModeMonth() {
      this.calendarMode = "Month";
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    calendarModeYear: function calendarModeYear() {
      this.calendarMode = "Year";
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    searchMode: function searchMode() {
      console.log('called searchMode');
      this.getPageNumber(this.pageNum, this.calendarMode, this.searchbar);
    },
    getSalesReportsByDate: function getSalesReportsByDate() {
      var _this = this;

      axios.post('/_system/php/api/salesreport/getByDate.php').then(function (response) {
        if (response.data.success) _this.salesReportsByDate = response.data.data;else console.error(response.data);
      }).catch(function (error) {
        console.log(error);
      });
    },
    getAllSales: function getAllSales() {
      var _this2 = this;

      console.log("tinawag kita");
      var formData = new FormData();
      formData.append("account_id", this.$store.getters.userId); //formData.append('pageNum', this.pageNum)

      axios.post("/_system/php/api/salesreport/getAll.php", formData).then(function (response) {
        if (response.data.success) {
          _this2.sales = response.data.data;
          console.log("tagumpay");
        } else console.error("bigo" + response.data);
      }).catch(function (error) {
        console.log(error);
      });
    },
    getPageCount: function getPageCount() {
      var _this3 = this;

      var formData = new FormData();
      formData.append("account_id", this.$store.getters.userId);
      axios.post("/_system/php/api/salesreport/getPageCount.php", formData).then(function (response) {
        if (response.data.success) _this3.pageCount = response.data.data;else console.error(response.data);
      }).catch(function (error) {
        console.log(error);
      });
    },
    getPageNumber: function getPageNumber(pageNum, calendarMode, searchbar) {
      var _this4 = this;

      console.log('Called GetPageNumber, value: ' + searchbar);
      var formData = new FormData();
      formData.append("account_id", this.$store.getters.userId);
      formData.append("pageNum", pageNum);
      formData.append("calendar_mode", calendarMode);
      formData.append("searchbar", searchbar);
      axios.post("/_system/php/api/salesreport/getByPage.php", formData).then(function (response) {
        console.log(response);
        if (response.data.success) _this4.sales = response.data.data;else console.error(response.data);
      }).catch(function (error) {
        console.error(error);
      });
    }
  }
};
