import Modal from "@/components/reusable/modal.vue";
export default {
  components: {
    modal: Modal
  },
  methods: {
    startPayment: function startPayment() {
      function buildSupportedPaymentMethodData() {
        // Example supported payment methods:
        return [{
          supportedMethods: "basic-card",
          data: {
            supportedNetworks: ["visa", "mastercard"],
            supportedTypes: ["debit", "credit"]
          }
        }];
      }

      var items = [];
      items.push({
        label: this.$store.getters.booking.package.package_name,
        amount: {
          currency: "PHP",
          value: this.$store.getters.booking.package.package_price
        }
      });

      for (var i = 0; i < this.$store.getters.booking.addons.length; i++) {
        items.push({
          label: this.$store.getters.booking.addons[i].addon_description + (this.$store.getters.booking.addons[i].addon_price == 0 ? " (FREE)" : ""),
          amount: {
            currency: "PHP",
            value: this.$store.getters.booking.addons[i].addon_price
          }
        });
      }

      var shoppingCartDetails = {
        id: "order-123",
        displayItems: items,
        total: {
          label: "Total",
          amount: {
            currency: "PHP",
            value: this.$store.getters.getBookingTotalPrice
          }
        }
      };

      function buildShoppingCartDetails() {
        return shoppingCartDetails;
      }

      var request = new PaymentRequest(buildSupportedPaymentMethodData(), buildShoppingCartDetails());
      var store = this.$store;
      var router = this.$router;
      request.show().then(function (paymentResponse) {
        // Here we would process the payment. For this demo, simulate immediate success:
        paymentResponse.complete("success").then(function () {
          // For demo purposes:
          alert("Payment Successful");
          console.log('Payment Successful');
          store.state.booking.payment_method = "CREDIT_CARD";
          store.dispatch("book");
          router.push("/dashboard");
        });
      });
    }
  }
};
