.wrapper {
	height: 100vh;
	background-color: grey;
	background-size: auto, auto;
	margin-left: 0px;
	border-left-width: 0px;
	padding-left: 0px;
	position: relative;
}

.view {
	height: 100%;
	background-color: blue;
}

#buttonLeftArrow {
	position: absolute;
	left: 2%;
	bottom: 45%;
	font-size: 70px;
	color: blanchedalmond;
	cursor: pointer;
}

svg > path {
	fill: #ffffff;
}

#buttonRightArrow {
	position: absolute;
	right: 2%;
	bottom: 45%;
	font-size: 70px;
	color: blanchedalmond;
	cursor: pointer;
}

.hPage {
	display: inline-block;
	height: 100%;
	width: 100%;
	padding: 30px;
	box-sizing: border-box;
	justify-content: center;
}

.boxContainer {
	display: -webkit-flex;
	display: flex;
	-webkit-align-items: baseline;
	-webkit-align-wrap: wrap;
	flex-wrap: wrap;
	align-items: baseline;
	width: calc(100vw - 50px);
	height: 100%;
	background-color: rgba(255, 255, 255, 0.1);
	border-radius: 10px;
	justify-content: center;
	overflow-y: auto;
}

.box {
	background-color: #ffffff22;
	width: 28%;
	height: calc(50% - 40px);
	margin: 20px;
}

.box img {
	width: 100%;
	height: 100%;
	box-shadow: 0px 3px 10px gray;
	transition: box-shadow 0.3s, transform 0.3s;
}

.box img:hover {
	box-shadow: 0px 6px 20px #555;
	transform: scale(1.4);
}

.desc {
	padding: 15px;
	text-align: center;
}

.surroundContainer {
	/* width: 400%;
    overflow: auto; */
	/* My own code */
	overflow: hidden;

	white-space: nowrap;
	background-position: -400px center;
}

::-webkit-scrollbar {
	display: none;
}

#indicator {
	position: fixed;
	top: 0;
	left: 0;
	height: 10px;
	background-color: red;
	z-index: 999;
}
